jQuery.fn.iframeresize=function(){return this.each(function(index,el){var setHeight=function(){var el=this;var $target=$(el).contents().find('body').first();var prop='padding:0px;margin:0px';$target.attr('style',prop);$(el).contents().find('html').attr('style',prop);var ht=$target.height();if(ht==0){ht=$target[0].scrollHeight;}
if(ht==0){ht=$target[0].clientHeight;}var add=($target.width()>$(el).width())?20:0;$(el).height(ht+add);};if($(this).attr('tagName').toLowerCase()=='iframe'){$(this).bind('iframeresize',setHeight);$(this).load(function(){$(el).trigger('iframeresize');});}});};